mapper.select:
SELECT ID , REPORT_TYPE , MODE_CONDITION , MODE_CONTENT , MODE_VALUES , MODE_AREA  FROM T_MODE 
WHERE 1 = 1 

	AND REPORT_TYPE = #{ reportType , jdbcType = VARCHAR}


	AND MODE_AREA = #{ modeArea , jdbcType = VARCHAR}


	AND ID = #{ reportType , jdbcType = VARCHAR}


ORDER BY PX ASC
